import React, { useState } from 'react'
import BankrollChart from './BankrollChart'

function App() {
  const [bankroll, setBankroll] = useState(1000)
  const [payoff, setPayoff] = useState(2)
  const [probability, setProbability] = useState(0.5)
  const [bets, setBets] = useState(10)

  const f = (payoff * probability - (1 - probability)) / payoff
  const betFraction = Math.min(f, 1)
  const growthRate = 1 + betFraction * probability

  const handleBankrollChange = (e) => {
    setBankroll(parseFloat(e.target.value))
  }

  const handlePayoffChange = (e) => {
    setPayoff(parseFloat(e.target.value))
  }

  const handleProbabilityChange = (e) => {
    setProbability(parseFloat(e.target.value))
  }

  const handleBetsChange = (e) => {
    setBets(parseInt(e.target.value))
  }

  return (
    <div>
      <h1>Bankroll Simulator</h1>
      <label>
        Starting Bankroll:
        <input type="number" value={bankroll} onChange={handleBankrollChange} />
      </label>
      <br />
      <label>
        Payoff:
        <input
          type="number"
          step="0.01"
          value={payoff}
          onChange={handlePayoffChange}
        />
      </label>
      <br />
      <label>
        Probability:
        <input
          type="number"
          step="0.01"
          value={probability}
          onChange={handleProbabilityChange}
        />
      </label>
      <br />
      <label>
        Number of Bets:
        <input type="number" value={bets} onChange={handleBetsChange} />
      </label>
      <br />
      <BankrollChart bankroll={bankroll} growthRate={growthRate} bets={bets} />
    </div>
  )
}

export default App
